def break_to_lines(lines):
    return lines.split('\n')


def process_word(word):
    return word.strip().strip('.').strip()


def process_line(line):
    if len(line) == 1 or line == "X, Y, Z" or not line:
        return
    words = line.split(':')[1].split(',')
    for word in words:
        yield process_word(word)


def process_text_block(block):
    lines = break_to_lines(block)
    with open("word_list.txt", 'w') as file:
        for line in lines:
            words = process_line(line)
            for word in words:
                file.write(word + '\n')


process_text_block(text)
